import {Injectable } from '@angular/core';

import {Product } from '../entities/product.entity';

@Injectable()
export class ProductService {
    
    private products: Product[];

    constructor() {
        this.products = [
            {id: 'p01', name: 'Galaxy S10', price: 1000, description: `128GB | 6.4" | 8GB RAM`, manufacturer: "Samsung",photo: "data:image/jpeg;base64,"},
            {id: 'p02', name: 'iPhone XS Max', price: 1199, description: `64GB | 6.5" | 4GB RAM`, manufacturer: "Apple",photo: "data:image/jpeg;base64,"},
            {id: 'p03', name: 'Note 9', price: 650, description: `128GB | 6.4" | 6GB RAM`, manufacturer: "Samsung",photo: "data:image/jpeg;base64,"},
            {id: 'p04', name: 'Mate 20 Pro', price: 829, description: `128GB | 6.5" | 6GB RAM`, manufacturer: "Huawei",photo: "data:image/jpeg;base64,"},
            //  {id: 'p03', name: 'name 3', price: 300,}
        ]
    }

    findAll(): Product[] {
        return this.products;
    }

    find(id: string): Product {
        return this.products[this.getSelectedIndex(id)];
    }

    private getSelectedIndex(id: string) {
        for (var i =0; i< this.products.length; i++)
        {
            if (this.products[i].id == id) {
                return i;
            }
        }
        return -1;
    }
}
                            